#!/bin/sh
# initial codebase taken from https://gist.github.com/demonbane/1065791
# Modified and Enchanced by: Anant Shrivastava
# http://anantshri.info
# Version 1.0
# * icon resized with aspect ratio preserved
# * Chromium / Chrome autoselected in following order
# 	$home/Applications/Chrome
# 	/Applications/Chrome
# 	$Home/Applications/Chromium
# 	/Applications/Chromium
# 	if no place found then app errors out.
# * Basic rudimentory URL Filter is in place
# * Optional parameter added to supply path to a existing/custom profile location
# * Default profile location kept at $HOME/Library/Application Support/Chromify/$url
# * Some default arguments can be specified example i like to keep disable-gpu
# * Plist enhanced to clearly state the application name
# * Plist has a version number also.
# * Plist also provides a note about chromifier app too

# TODO:
# 1. add an option to provide custom arguments for a specific application builds
# 2. disable debug output
# 3. Check if profile directory is writable if dir exist otherwise check parent writable if mkdir has to happen
# 4. add a default icon in case icon file is not present
# Take a user input asking if they want to use stock icon.

# Issues identified so far
# 1. two apps with same profile are not listed as two seperate entities.

# Define variables
url_regex='(https?|ftp|file)://[-A-Za-z0-9\+&@#/%?=~_|!:,.;]*[-A-Za-z0-9\+&@#/%=~_|]'
# --disable-gpu
default_arg=""
custom_arg=""
appRoot="$HOME/Applications"
default_icon=""

# Some functions to avoid repitition.

function err {
	echo "[-] $*"
}

function output {
	echo "[+] $*"
}

function help {
	err "Correct Usage is : "
	err $0 '"APPNAME" "APP_URL" "<ICON_LOCATION>" "<PROFILE_LOCATION>" "<custom_arg>"'
	err "<PROFILE_LOCATION> and <custom_arg> are optional"
	err "if no input provided then standard location used"	
}
function header {
	output "chromifier : Chrome / chromium single window app builder with dedicated profile"
	output  "initial script code by demonbane"
	output "Modified and Enchanced by: Anant Shrivastava"
	output "https://anantshri.info | anant@anantshri.info"
}
function chrome_set {
	if [ -f "$HOME/Applications/Chromium.app/Contents/MacOS/Chromium" ]
then
	output "Chromium Found in $HOME/Applications using it"
	BROWSER="$HOME/Applications/Chromium.app/Contents/MacOS/Chromium"
elif [ -f "/Applications/Chromium.app/Contents/MacOS/Chromium" ]
then
	output "Chromium Found in /Applications using it"
	BROWSER="/Applications/Chromium.app/Contents/MacOS/Chromium"
elif [ -f "$HOME/Applications/Google Chrome.app/Contents/MacOS/Google Chrome" ]
then
	output "Chrome Found in $HOME/Applications"
	BROWSER="$HOME/Applications/Google Chrome.app/Contents/MacOS/Google Chrome"
elif [ -f "/Applications/Google Chrome.app/Contents/MacOS/Google Chrome" ]
then
	output "Chrome Found in /Applications"
	BROWSER="/Applications/Google Chrome.app/Contents/MacOS/Google Chrome"
else
	err "Chromium or Chrome browser was not found to be installed"
	err "Please install it to use this script properly"
	exit
fi

}
if [ $# -eq 1 ]
then	
	header
 	help
 	exit
fi
if [ $# -ge 2 ]
then
	# echo "Execution"
	name=$1
	url=$2
	icon=$3

	if [[ $url =~ $url_regex ]]
	then
		output "URL Looks valid"
	else
		err "URL Appears invalid"
		help
		exit
	fi
	if [ -f "$icon" ]
	then
		output "Icon file exist"
		file $icon | cut -f2 -d","
		iconh=`file $icon | cut -f2 -d","|cut -f1 -d"x"`
		iconw=`file $icon | cut -f2 -d","|cut -f2 -d"x"`
		resp=`expr $iconh / $iconw`
		if [ $resp -eq 1 ]
		then
			output "Image is square same scalled image will be used"
		else
			err "Image is not square then it will be resized with aspect ratio maintained"
		fi
	else
		err "Icon file is missing"
		err "Using default image"
		icon_dir=`mktemp -dt "chromifier.XXXXXXXXXXXX"`
		icon=$icon_dir"/icon.png"
		output $icon_dir
		output $icon
		text=`echo "$default_icon" | base64 --decode > $icon`
		output $text
	fi
	if [ $# -ge 4 ]
	then
		if [ -d "$4" ]
		then
			output "Profile directory already exist"
			output "Using it"
			profile=$4
		else
			err "Profile Directory Doesn't exist"
			output "Directory will be created"
			if [ -z $4 ]
			then
				err "Profile is empty link so overriding with default"
				profile="$HOME/Library/Application Support/Chromify/$name"
			fi
			profile=$4			
		fi
	else
			err "Profile Directory not specified"
			output "Directory will be created"
			profile="$HOME/Library/Application Support/Chromify/$name"
	fi

	if [ $# -ge 5 ]
	then
		output "Custom arguments specified"
		output "These will be placed in the command"
		custom_arg=$5
	fi
else
	header
	err "Incorrect number of options specified"
	help
	exit
fi
chrome_set
 
 
 
 
resourcePath="$appRoot/$name.app/Contents/Resources"
execPath="$appRoot/$name.app/Contents/MacOS"
plistPath="$appRoot/$name.app/Contents/Info.plist"
# make the directories
mkdir -p "$resourcePath" "$execPath" "$profile"
 
# convert the icon and copy into Resources
if [ -f "$icon" ] ; then
sips -s format tiff "$icon" --out "$resourcePath/icon.tiff" --resampleHeightWidth 128 128 >& /dev/null
tiff2icns -noLarge "$resourcePath/icon.tiff" >& /dev/null
fi
 
# create the executable
cat > "$execPath/$name" <<EOF
#!/bin/sh
exec "$BROWSER" $default_arg $custom_arg --app="$url" --user-data-dir="$profile" "\$@"
EOF
chmod +x "$execPath/$name"
 
# create the Info.plist
cat > "$plistPath" <<EOF
<?xml version="1.0" encoding="UTF-8"?>
<!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" “http://www.apple.com/DTDs/PropertyList-1.0.dtd”>
<plist version=”1.0″>
<dict>
<key>CFBundleExecutable</key>
<string>$name</string>
<key>CFBundleIconFile</key>
<string>icon</string>
<key>CFBundleName</key>
<string>$name</string>
<key>CFBundleIdentifier</key>
<string>com.chromifier.$name</string>
<key>CFBundleVersion</key>
<string>1.0.0</string>
<key>CFBundleDisplayName</key>
<string>$name Chromified Application</string>
<key>CFBundleGetInfoString</key>
<string>Created using Chromifier by Anant Shrivastava</string>
</dict>
</plist>
EOF

output "Thanks for using the chromifier"
output "$appRoot/$name.app Created"
output "Enjoy"
