#!/bin/bash
function help {
	echo "Correct Usage is : "
	echo $0 '"APPNAME" "APP_URL" "<ICON_LOCATION>" "<PROFILE_LOCATION>" "<custom_arg>"'
	echo "<PROFILE_LOCATION> and <custom_arg> are optional"
	echo "if no input provided then standard location used"
}
# if we don't have any arguments lets not waste our time
if [ $# -lt 1 ]
then
	help
	exit
fi
url_regex='(https?|ftp|file)://[-A-Za-z0-9\+&@#/%?=~_|!:,.;]*[-A-Za-z0-9\+&@#/%=~_|]'
default_arg="--disable-gpu --disable-sync"
custom_arg=""
name=$1
url=$2
icon=$3
profile=$4
custom_arg=$5
default_icon=""
echo $name
echo $url
echo $icon
echo $profile
echo $custom_arg
if [ -z $profile  ]
then
	echo "No profile lets pick default"
	profile="$HOME/.chromifier/$name"
fi
echo "Chromifier for Linux"
echo "Linux Detected: Creating a per user application"
echo "Profile: $profile"
if [ -f "/usr/bin/chromium-browser" ]
then
	BROWSER="/usr/bin/chromium-browser"
elif [ -f "/usr/bin/google-chrome" ]
then
    BROWSER="/usr/bin/google-chrome"
else
	echo "Chromium/Chrome browser was not found to be installed"
	echo "Please install it to use this script properly"
	BROWSER="/usr/bin/chromium-browser"
fi
echo "Chrome/Chromium detected at $BROWSER"
mkdir -p $profile
if [ -f $icon ]
then
	echo "Icon file found"
	echo "copying it to profile folder"
	cp $icon $profile/icon.png
else
	echo "Icon Missing"
	echo "$default_icon" | base64 --decode > $profile/icon.png
fi
cat >"$HOME/.local/share/applications/$name.desktop" <<EOF
#!/usr/bin/env xdg-open
[Desktop Entry]
Type=Application
Name=$name
Exec="$BROWSER" $default_arg $custom_arg --app="$url" --class=APP_$name --user-data-dir="$profile" "\$@"
Icon=$profile/icon.png
StartupWMClass=APP_$name
Categories=Application;
EOF
